package SubInterview;

public class Repeatedchar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="chanduuu";
		int count=0;
		for(int i=0;i<name.length();i++)
		{
			if(name.charAt(i) == 'u')
			{
				count++;}
		}
				System.out.println(count);
			}
		}
		
		

	


